# Exercice 1 
def saisir_message():
    L = list()
    for i in range(4):
        L.append(input("donner la ligne {} ".format(i+1)))
    try:
        with open("message.txt","w") as f:
            for i in range(4):
                f.write(L[i]+'\n')
    except IOError as err:
        print(err)

# Question 2
def lire_message():
    try:
        with open("message.txt") as f:
            print(f.read())
    except IOError as err:
        print(err)

# Question 3
def fcopie(f1,f2):
    try :
        with open(f1) as f1, open(f2,"w") as f2 :
            Lignes = f1.readlines()
            for ligne in Lignes:
                f2.write(ligne.upper())
    except IOError as err :
        print(err)
fcopie("message.txt", "message1.txt")

# Question 4
def ligne_plus_longue(f):
    try:
        with open(f) as f:
            Lignes = f.readlines()
            MaxLigne = Lignes[0]
            for ligne in range(1,len(Lignes)):
                if len(MaxLigne) < len(Lignes[ligne]):
                    MaxLigne = Lignes[ligne]
            return MaxLigne
    except IOError as err:
        print(err)

# Question 5
def extraction(f1,f2):
    try :
        with open(f1) as f1, open(f2,"w") as f2 :
            Lignes = f1.readlines()
            for ligne in Lignes:
                if ligne.startswith ('e'):
                    f2.write(ligne.upper())
    except IOError as err :
        print(err)


# Question 6
def fusion(f1,f2,frslt):
    try:
        with open(f1) as f1,open(f2) as f2,open(frslt,"w") as f3:
            Lignes1 = f1.readlines()
            Lignes2 = f2.readlines()
            a = min(len(Lignes1),len(Lignes2))
            for i in range(a):
                f3.write(Lignes1[i]+Lignes2[i])
            for i in range(a,len(Lignes1)):
                f3.write(Lignes1[i])
            for i in range(a,len(Lignes2)):
                f3.write(Lignes2[i])
    except IOError as err:
        print(err)
